import { ZoomMtg } from '@zoomus/websdk';
import React, { useState } from 'react';
import '../App.css';
import { authEndpoint } from '../Constants';

ZoomMtg.setZoomJSLib('https://source.zoom.us/2.11.5/lib', '/av');
ZoomMtg.preLoadWasm();
ZoomMtg.prepareWebSDK();
// loads language files, also passes any error messages to the ui
ZoomMtg.i18n.load('en-US');
ZoomMtg.i18n.reload('en-US');

const JoinMeeting = () => {
  var sdkKey = 'eSDulDrBSwmK3FyDSAVa6Q';
  var userEmail = '';
  var leaveUrl = 'http://localhost:3000';

  const [inputs, setInputs] = useState({});

  const getSignature = (e) => {
    e.preventDefault();
    fetch(`${authEndpoint}/api/zoom/get_signature`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        meetingNumber: inputs.meetingNumber,
        role: 0,
      }),
    })
      .then((res) => res.json())
      .then((response) => {
        startMeeting(response.signature);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  function startMeeting(signature) {
    document.getElementById('zmmtg-root').style.display = 'block';
    ZoomMtg.init({
      leaveUrl: leaveUrl,
      success: (success) => {
        console.log(success);

        ZoomMtg.join({
          signature: signature,
          sdkKey: sdkKey,
          meetingNumber: inputs.meetingNumber,
          passWord: inputs.passWord,
          userName: inputs.userName,
          userEmail: userEmail,
          success: (success) => {
            console.log(success);
          },
          error: (error) => {
            console.log(error);
          },
        });
      },
      error: (error) => {
        console.log(error);
      },
    });
  }

  const handleChange = (event) => {
    const {
      target: { name, value },
    } = event;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  return (
    <>
      <div className="App">
        <h2 className="meeting-header">Zoom Meeting</h2>
        <div className="meeting-container">
          <i className="helpText">
            **Please fill all the detail before join the meeting.
          </i>
          <div className="meeting-grid">
            <label for="meetingId">Meeting ID</label>
            <input
              type="text"
              id="meetingId"
              placeholder="Meeting ID"
              value={inputs.meetingNumber || ''}
              name="meetingNumber"
              onChange={handleChange}
            />
          </div>
          <div className="meeting-grid">
            <label for="passCode">Passcode</label>
            <input
              type="text"
              placeholder="Passcode"
              value={inputs.passWord || ''}
              name="passWord"
              onChange={handleChange}
            />
          </div>
          <div className="meeting-grid">
            <label for="username">User Name</label>
            <input
              type="text"
              placeholder="User name"
              value={inputs.userName || ''}
              name="userName"
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="action-container">
          <button className="join-meeting-button" onClick={getSignature}>
            Join Meeting
          </button>
        </div>
      </div>
    </>
  );
};

export default JoinMeeting;
