import React, { useState } from 'react';
import '../App.css';
import { getRefreshToken } from '../CommonUtils/CommonUtils';

const ScheduleMeeting = () => {
  let authEndpoint = 'http://localhost:4000';
  const [inputs, setInputs] = useState({});
  // const [duration, setDuration] = useState(40);

  // const handleDurationChange = (event) => {
  //   setDuration(event.target.value);
  // };

  const handleChange = (event) => {
    const {
      target: { name, value },
    } = event;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const addMeeting = (event) => {
    event.preventDefault();
    const reqParam = {
      ...inputs,
      start_time: new Date(inputs.start_time).toISOString(),
      duration: 40,
    };
    const zoomToken = sessionStorage.getItem('zoomToken');
    const zoomRefreshToken = sessionStorage.getItem('zoomRefreshToken');
    fetch(`${authEndpoint}/api/zoom/add_meeting`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: zoomToken,
      },
      body: JSON.stringify(reqParam),
    })
      .then((res) => res.json())
      .then((response) => {
        if (
          response?.code === 124 &&
          response?.message === 'Access token is expired.'
        ) {
          getRefreshToken(zoomRefreshToken);
        } else {
          window.location.replace('/');
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <>
      <div className="App">
        <h2 className="meeting-header">Schedule Meeting</h2>
        <form onSubmit={addMeeting}>
          <div className="meeting-container">
            <i className="helpText">
              **Please fill all the detail before schedule the meeting.
            </i>

            <div className="meeting-grid">
              <label for="topic">Topic</label>
              <input
                type="text"
                id="topic"
                placeholder="Meeting topic"
                value={inputs.topic || ''}
                name="topic"
                onChange={handleChange}
                required={true}
              />
            </div>
            <div className="meeting-grid">
              <label for="agenda">Agenda</label>
              <input
                type="text"
                id="agenda"
                placeholder="Meeting agenda"
                value={inputs.agenda || ''}
                name="agenda"
                onChange={handleChange}
                required={true}
              />
            </div>

            {/* <div className="meeting-grid">
              <label for="duration">Duration</label>
              <select
                onChange={handleDurationChange}
                name="duration"
                placeholder="Meeting duration"
                required={true}
              >
                <option value="-1">Select</option>
                <option value={40} selected>
                  40 mins
                </option>
                <option value={60}>60 mins</option>
              </select>
            </div> */}

            <div className="meeting-grid">
              <label for="start_time">Schedule Date</label>
              <input
                type="text"
                id="start_time"
                placeholder="mm/dd/yyyy HH:mm"
                value={inputs.start_time || ''}
                name="start_time"
                onChange={handleChange}
                required={true}
              />
            </div>

            <div className="meeting-grid">
              <label for="start_time">Schedule time</label>
              <input
                type="text"
                id="start_time"
                placeholder="mm/dd/yyyy HH:mm"
                value={inputs.start_time || ''}
                name="start_time"
                onChange={handleChange}
                required={true}
              />
            </div>
          </div>
          <div className="action-container">
            <button type="submit" className="join-meeting-button">
              Schedule Meeting
            </button>
          </div>
        </form>
      </div>
    </>
  );
};

export default ScheduleMeeting;
