import { Fragment, useEffect, useState } from 'react';
import { getRefreshToken } from '../CommonUtils/CommonUtils';
import {
  ACCESS_TOKEN_EXPIRED,
  NODE_API_END_POINT,
  ZOOM_ERROR_CODE,
} from '../Constants';
import './MeetingList.css';

const MeetingList = () => {
  const [meetingList, setMeetingList] = useState([]);

  const getAllMeetings = () => {
    const zoomToken = sessionStorage.getItem('zoomToken');
    const zoomRefreshToken = sessionStorage.getItem('zoomRefreshToken');
    fetch(`${NODE_API_END_POINT}/api/zoom/meetings`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: zoomToken,
      },
    })
      .then((res) => res.json())
      .then((response) => {
        if (response?.meetings?.length) {
          setMeetingList(response?.meetings);
        } else if (
          response?.code === ZOOM_ERROR_CODE &&
          response?.message === ACCESS_TOKEN_EXPIRED
        ) {
          setMeetingList([]);
          getRefreshToken(zoomRefreshToken);
        }
      })
      .catch((error) => {
        setMeetingList([]);
        console.error(error);
      });
  };

  useEffect(() => {
    if (!meetingList.length) {
      getAllMeetings();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [meetingList]);

  const dateFormat = (date) => {
    let dateString =
      ('0' + (date.getMonth() + 1)).slice(-2) +
      '/' +
      ('0' + date.getDate()).slice(-2) +
      '/' +
      date.getFullYear() +
      ' ' +
      formatAMPM(date);
    return dateString;
  };

  function formatAMPM(date) {
    var hours = date.getHours();
    var minutes = date.getMinutes();
    var ampm = hours >= 12 ? 'PM' : 'AM';
    hours = hours % 12;
    hours = hours ? (hours < 10 ? '0' + hours : hours) : 12; // the hour '0' should be '12'
    minutes = minutes < 10 ? '0' + minutes : minutes;
    var strTime = hours + ':' + minutes + ' ' + ampm;
    return strTime;
  }

  return (
    <>
      <div className="table_div">
        <table border="1">
          <thead>
            <tr>
              <th>Meeting Id</th>
              <th>Topic</th>
              <th>Agenta</th>
              <th>Duration</th>
              <th>Start Time</th>
              <th>Action</th>
            </tr>
          </thead>
          {meetingList?.length ? (
            meetingList.map((item) => (
              <Fragment key={item?.uuid}>
                <tbody>
                  <tr>
                    <td>{item?.id}</td>
                    <td>{item?.topic}</td>
                    <td>{item?.agenda || '-'}</td>
                    <td>{item?.duration} mins</td>
                    <td>{dateFormat(new Date(item?.start_time))}</td>
                    <td>
                      <a
                        href={item?.join_url}
                        target="_blank"
                        rel="noreferrer"
                        className="start-meeting-button"
                      >
                        Start meeting
                      </a>
                    </td>
                  </tr>
                </tbody>
              </Fragment>
            ))
          ) : (
            <tbody>
              <tr>
                <td colSpan={6} style={{ textAlign: 'center' }}>
                  No record found
                </td>
              </tr>
            </tbody>
          )}
        </table>
      </div>
    </>
  );
};
export default MeetingList;
